@main def daySix2022: Unit = {
  val initTime = System.currentTimeMillis
  println("Welcome to day six of the Advent of Code!")
  println("The amount of chars that need to be processed before the start of packet is detected is " + DaySix.findTheFirstMarker(input, 4))
  println("The amount of chars that need to be processed before the message is detected is " + DaySix.findTheFirstMarker(input, 14))
  val elapsedTime = System.currentTimeMillis-initTime
  println("This took " + elapsedTime + " milliseconds")
}


object DaySix {
  def findTheFirstMarker(buffer: String, amountOfDistinctChars: Int) : Int = {
    val array = buffer.toCharArray
    var counter = amountOfDistinctChars
    while (counter-1 < array.length) {
      val previousChars = array.slice(counter-amountOfDistinctChars, counter)
      if (onlyUniqueCharsInString(previousChars)) return counter else counter += 1
    }
    0
  }

  def onlyUniqueCharsInString(previousChars: Array[Char]) = {
    previousChars.distinct.length == previousChars.length
  }
}

val input: String = ""