@main def dayTwo2022: Unit = {
  val initTime = System.currentTimeMillis
  val totalScore = DayTwo.calculateTheTotalScoreForMultipleRoundsOfRockPaperScissors(input)
  println("The total score for the games of Rock Paper Scissors is " + totalScore)
  val updatedTotalScore = DayTwo.calculateTheUpdatedTotalScoreForMultipleRoundsOfRockPaperScissors(input)
  println("The updated total score for the games of Rock Paper Scissors is " + updatedTotalScore)
  val elapsedTime = System.currentTimeMillis-initTime
  println("This took " + elapsedTime + " milliseconds")
}


object DayTwo {

  def calculateTheTotalScoreForMultipleRoundsOfRockPaperScissors(input: List[Tuple2[Char, Char]]): Int = {
    val totalScore = input.map(whatIsTheScoreForTheOutcomeOfThisRound(_)).sum
    return totalScore
  }

  def calculateTheUpdatedTotalScoreForMultipleRoundsOfRockPaperScissors(input: List[Tuple2[Char, Char]]): Int = {
    val totalScore = input.map(whatIsTheUpdatedScoreForTheOutcomeOfThisRound(_)).sum
    return totalScore
  }

  def whatIsTheUpdatedScoreForTheOutcomeOfThisRound(game: Tuple2[Char, Char]): Int = {
    game match
    case ('A', 'X')
    => 0 + 3 // Loose against Rock => Scissors
    case ('A', 'Y')
    => 3 + 1 // Draw against Rock => Rock
    case ('A', 'Z')
    => 6 + 2 // Win against Rock => Paper
    case ('B', 'X')
    => 0 + 1 // Loose against Paper => Rock
    case ('B', 'Y')
    => 3 + 2 // Draw against Paper => Paper
    case ('B', 'Z')
    => 6 + 3 // Win against Paper  => Scissors
    case ('C', 'X')
    => 0 + 2 // Loose against Scissors => Paper
    case ('C', 'Y')
    => 3 + 3 // Draw against Scissors => Scissors
    case ('C', 'Z')
    => 6 + 1 // Win against Scissors => Rock
    case _ => 0
  }

  def whatIsTheScoreForTheOutcomeOfThisRound(game: Tuple2[Char, Char]): Int = {
    game match
    case ('A', 'X')
    => 3 + 1 // Rock vs Rock = Draw
    case ('A', 'Y')
    => 6 + 2 // Rock vs Paper = I win
    case ('A', 'Z')
    => 0 + 3 // Rock vs Scissors = I loose
    case ('B', 'X')
    => 0 + 1 // Paper vs Rock = I loose
    case ('B', 'Y')
    => 3 + 2 // Paper vs Paper = Draw
    case ('B', 'Z')
    => 6 + 3 // Paper vs Scissors = I win
    case ('C', 'X')
    => 6 + 1 // Scissors vs Rock = I win
    case ('C', 'Y')
    => 0 + 2 // Scissors vs Paper = I loose
    case ('C', 'Z')
    => 3 + 3 // Scissors vs Scissors = Draw
    case _ => 0
  }
}

